def binary_to_decimal(num):
    result = 0
    for i in range(len(num)):
        digit = int(num[i])
        result += digit * (2 ** (3 - i))
    return result

def decimal_to_binary(num):
    result = ""
    for i in reversed(range(len(num))):
        digit = int(num[i])
        result += str(digit)
        if i == 0:
            result += "a"
        else:
            result += "b"
    return result

def num_to_binary(num):
    base_10 = binary_to_decimal(num)
    base_8 = base_10[-3:]
    return base_8
