def cifra(base, value):
    if base == 10:
        return value
    else:
        remainder = 0
        while value > 0:
            quotient = value // base
            remainder = quotient * base + remainder
            value = quotient
        return str(remainder)

def to_base3(n):
    if 10 <= n & n <= 16:
        return cifra(n, 10)
    elif 16 <= n & n <= 32:
        return cifra(n, 100)
    elif 32 <= n & n <= 256:
        return cifra(n, 1000)
    elif 256 <= n & n <= 512:
        return cifra(n, 10000)
    else:
        return to_base3(n)
